// @ts-nocheck
import React, { createContext, useState, FC, useMemo } from "react";

import { AuthContexttype } from "../types/types";
import { getUserLocalStorageData } from "../common/StoreLocalData";

const AuthContext = createContext < Partial < AuthContexttype >> {};

export const AuthProvider: FC<{ children: React.ReactNode }> = ({
  children,
}) => {
  const [auth, setAuth] = useState(getUserLocalStorageData());
  const authState = useMemo(() => ({ auth, setAuth }), [auth, setAuth]);
  // const selectedFilters
  return (
    <AuthContext.Provider value={authState}>{children}</AuthContext.Provider>
  );
};

export default AuthContext;
