import React, { useState, useEffect, useContext } from "react";
import { Navigate, useNavigate } from "react-router-dom";

import {
  loginFormConstantants,
  PATH_ROOT,
  registerFormConstantants,
} from "../constants";
import { getStudents, signIn } from "../../api";
import AuthContext from "../../context/AuthProvider";

export const Login = () => {
  const [loginFormFields, setLoginFormFields] = useState({});
  const [registerFormFields, setRegisterFormFields] = useState({});
  const [showForm, setShowForm] = useState("login");
  const { setAuth } = useContext(AuthContext);

  const navigate = useNavigate();

  //   useEffect(() => {
  //     const token = localStorage.getItem("token");
  //     if (token) {
  //       //   console.log("token", token);
  //       //   return <Navigate path={PATH_ROOT} exact replace={true} />;
  //     }
  //   }, []);

  const renderLoginForm = (formFieldConstants) => {
    return formFieldConstants?.map((item) => {
      return (
        <div className="col-12" key={item.key}>
          <div className="form-group">
            <label>{item?.label}</label>
            <input
              type={item?.type}
              placeholder={item?.label}
              className="form-control"
              name={item?.key}
              onChange={(e) => {
                showForm === "login"
                  ? setLoginFormFields({
                      ...loginFormFields,
                      [item.key]: e.target.value,
                    })
                  : setRegisterFormFields({
                      ...registerFormFields,
                      [item.key]: e.target.value,
                    });
              }}
            />
          </div>
        </div>
      );
    });
  };

  const handleLogin = () => {
    console.log("loginFormFields", loginFormFields);
    // if (loginFormFields?.email === "p" && loginFormFields?.password === "p") {
    //   navigate("/");
    //   console.log("login success");
    // }

    signIn(loginFormFields)?.then((res) => {
      if (res.status === 200) {
        console.log("resss,", res);
        localStorage.setItem("token", res.data.accessToken);
        localStorage.setItem("username", res.data.username);
        // <Navigate to={PATH_ROOT} exact replace={true} />;
        const updatedUser = {
          token: res.data.accessToken,
          username: res.data.username,
        };
        setAuth?.(updatedUser);
        navigate("/", { replace: true });
      }
    });
  };

  const handleRegister = () => {
    console.log("registerFormFields", registerFormFields);
  };

  const renderFormLink = (text, formValue) => {
    return (
      <>
        <div
          className="register-login-link"
          onClick={() => setShowForm(formValue)}
        >
          {text}
        </div>
        {formValue !== "login" ? (
          <div className="register-login-link forgot-link">
            I forgot my password
          </div>
        ) : (
          ""
        )}
      </>
    );
  };
  return (
    <>
      <div className="content-wrapper">
        <section className="content">
          <div className="login-wrapper">
            <section className="content-header">
              <h1 className="login-heading">Sign In</h1>
            </section>
            <div className="box">
              <div className="box-body ">
                <div className="row">
                  {renderLoginForm(
                    showForm === "login"
                      ? loginFormConstantants
                      : registerFormConstantants
                  )}
                </div>

                <button
                  type="submit"
                  className="btn btn-success btn-block btn-flat r-btn"
                  onClick={() =>
                    showForm === "login" ? handleLogin() : handleRegister()
                  }
                >
                  {showForm === "login" ? "Sign In" : "Register"}
                </button>
                {showForm === "login"
                  ? renderFormLink("Register a new membership", "register")
                  : renderFormLink("I already have a membership", "login")}
              </div>
            </div>
          </div>
        </section>
      </div>
    </>
  );
};
