import React from "react";

export const Login = () => {
  return (
    // <div className="content-wrapper login-wrapper">
    //   <div className="hold-transition login-page login">
    //     <div class="login-box login-wrapper">
    //       <div class="login-logo">
    //         <a href="../../index2.html">
    //           <b>Admin</b>LTE
    //         </a>
    //       </div>
    //       <div class="card">
    //         <div class="card-body login-card-body">
    //           <p class="login-box-msg">Sign in to start your session</p>

    //           <form action="../../index3.html" method="post">
    //             <div class=" mb-3">
    //               <input
    //                 type="email"
    //                 class="form-control"
    //                 placeholder="Email"
    //               />
    //               <div class="input-group-append">
    //                 <div class="input-group-text">
    //                   <span class="fas fa-envelope"></span>
    //                 </div>
    //               </div>
    //             </div>
    //             <div class="input-group mb-3">
    //               <input
    //                 type="password"
    //                 class="form-control"
    //                 placeholder="Password"
    //               />
    //               <div class="input-group-append">
    //                 <div class="input-group-text">
    //                   <span class="fas fa-lock"></span>
    //                 </div>
    //               </div>
    //             </div>
    //             {/* <div class="row">
    //               <div class="col-8">
    //                 <div class="icheck-primary">
    //                   <input type="checkbox" id="remember" />
    //                   <label for="remember">Remember Me</label>
    //                 </div>
    //               </div>
    //               <div class="col-4">
    //                 <button type="submit" class="btn btn-primary btn-block">
    //                   Sign In
    //                 </button>
    //               </div>
    //             </div> */}
    //           </form>

    //           <div class="social-auth-links text-center mb-3">
    //             <a href="#" class="btn btn-block btn-primary">
    //               <i class="fab fa-facebook mr-2"></i> Sign In
    //             </a>
    //           </div>

    //           {/* <p class="mb-1">
    //             <a href="forgot-password.html">I forgot my password</a>
    //           </p>
    //           <p class="mb-0">
    //             <a href="register.html" class="text-center">
    //               Register a new membership
    //             </a>
    //           </p> */}
    //         </div>
    //       </div>
    //     </div>
    //   </div>
    // </div>
    <>
      <div className="content-wrapper">
        <section className="content-header">
          <h1>Add New Customer</h1>
        </section>
        <section className="content">
          <div className="row">
            <div className="col-xs-12">
              <div className="box">
                {error && <p className="error"> {error} </p>}
                <div className="box-header">
                  <h3 className="box-title"> Customer Master</h3>
                </div>
                <div className="box-body">
                  <div className="row">
                    <div className="col-md-6">
                      <div className="form-group required">
                        <label className="control-label">Customer Name</label>
                        <input
                          type="text"
                          className="form-control"
                          name="name"
                          required
                          value={customer.name}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="form-group required">
                        <label className="control-label">Contact Number</label>
                        <input
                          type="text"
                          className="form-control"
                          name="contactNo"
                          required
                          value={customer.contactNo}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-md-6">
                      <div className="form-group required">
                        <label className="control-label">Address</label>
                        <input
                          type="text"
                          className="form-control"
                          name="address"
                          value={customer.address}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                    <div className="col-md-3">
                      <div className="form-group required">
                        <label className="control-label">City</label>
                        <input
                          type="text"
                          className="form-control"
                          name="city"
                          value={customer.city}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                    <div className="col-md-3">
                      <div className="form-group">
                        <label>Pin</label>
                        <input
                          type="text"
                          className="form-control"
                          name="pin"
                          value={customer.pin}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-md-6">
                      <div className="form-group">
                        <label>State</label>
                        <input
                          type="text"
                          className="form-control"
                          name="stateId"
                          value={customer.stateId}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="form-group required">
                        <label>Email</label>
                        <input
                          type="text"
                          className="form-control"
                          name="email"
                          value={customer.email}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-md-6">
                      <div className="form-group">
                        <label>PAN</label>
                        <input
                          type="text"
                          className="form-control"
                          name="pan"
                          value={customer.pan}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                    <div className="col-md-6">
                      <div className="form-group required">
                        <label>Current Outstanding Balance</label>
                        <input
                          type="text"
                          className="form-control"
                          name="totalOutBal"
                          value={customer.totalOutBal}
                          onChange={handleInputChange}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-3">
                      <button
                        type="submit"
                        className="btn btn-success btn-block btn-flat r-btn"
                        onClick={saveCustomer}
                      >
                        Save
                      </button>
                    </div>
                    <div className="col-md-6"></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    </>
  );
};
